/-- The invariance of the metric under the group action. -/
def MulActionTensor.metric_inv {R : Type} [CommSemiring R] {G : Type} [Monoid G] {𝓣 : TensorStructure R} [self : MulActionTensor G 𝓣] (μ : 𝓣.Color) (g : G) : (TensorProduct.map ((MulActionTensor.repColorModule μ) g) ((MulActionTensor.repColorModule μ) g)) (𝓣.metric μ) =
  𝓣.metric μ

/-- The contraction of a vector with its dual is invariant under the group action. -/
def MulActionTensor.contrDual_inv {R : Type} [CommSemiring R] {G : Type} [Monoid G] {𝓣 : TensorStructure R} [self : MulActionTensor G 𝓣] (μ : 𝓣.Color) (g : G) : 𝓣.contrDual μ ∘ₗ TensorProduct.map ((MulActionTensor.repColorModule μ) g) ((MulActionTensor.repColorModule (𝓣.τ μ)) g) =
  𝓣.contrDual μ
